select a.student_id ,a.student_name,s.subject_name, count(e.student_id ) as attended_exams  from Students a cross join Subjects as s left join examinations e on a.student_id=e.student_id and s.subject_name=e.subject_name  GROUP BY a.student_id,a.student_name,s.subject_name
ORDER BY a.student_id, s.subject_name;